### Benchmark Setup
```rust
// benches/storage_perf.rs
use criterion::{black_box, criterion_group, criterion_main, Criterion, BenchmarkId};

fn benchmark_create(c: &mut Criterion) {
    let mut group = c.benchmark_group("create");

    for size in [10, 100, 1000, 10000].iter() {
        group.bench_with_input(
            BenchmarkId::from_parameter(size),
            size,
            |b, &size| {
                b.iter_batched(
                    || setup_db(),
                    |mut storage| {
                        for i in 0..size {
                            let title = format!("Issue {}", i);
                            storage.create_issue(&title, IssueType::Task, 2).unwrap();
                        }
                    },
                    BatchSize::SmallInput,
                );
            },
        );
    }

    group.finish();
}

fn benchmark_list(c: &mut Criterion) {
    let mut group = c.benchmark_group("list");

    // Pre-populate databases of different sizes
    for size in [100, 1000, 10000].iter() {
        let storage = setup_db_with_issues(*size);

        group.bench_with_input(
            BenchmarkId::from_parameter(size),
            &storage,
            |b, storage| {
                b.iter(|| {
                    black_box(storage.list_issues(ListQuery::default()).unwrap())
                });
            },
        );
    }

    group.finish();
}

fn benchmark_ready_query(c: &mut Criterion) {
    let mut group = c.benchmark_group("ready");

    // Complex dependency graph
    for (issues, deps) in [(100, 200), (1000, 2000), (10000, 20000)].iter() {
        let storage = setup_db_with_deps(*issues, *deps);

        group.bench_with_input(
            BenchmarkId::new("issues_deps", format!("{}_{}", issues, deps)),
            &storage,
            |b, storage| {
                b.iter(|| {
                    black_box(storage.get_ready_issues(ReadyFilters::default()).unwrap())
                });
            },
        );
    }

    group.finish();
}

criterion_group!(
    benches,
    benchmark_create,
    benchmark_list,
    benchmark_ready_query,
);
criterion_main!(benches);
