use assert_cmd::Command;
use predicates::prelude::*;
use tempfile::TempDir;
use std::path::Path;
use tracing::{info, debug, error};

/// Create a br command pointing to a specific beads directory
pub fn br_cmd(beads_dir: &Path) -> Command {
    let mut cmd = Command::cargo_bin("br").unwrap();
    cmd.env("BEADS_DIR", beads_dir);
    cmd.env("NO_COLOR", "1"); // Disable colors for predictable output
    cmd.env("RUST_LOG", "beads_rust=debug"); // Enable debug logging
    cmd
}

/// Initialize br in a temp directory
pub fn init_beads() -> (Command, TempDir) {
    let dir = TempDir::new().unwrap();
    let beads_dir = dir.path().join(".beads");

    let mut cmd = br_cmd(&beads_dir);
    cmd.arg("init")
        .assert()
        .success();

    info!(?beads_dir, "Initialized test beads directory");
    (br_cmd(&beads_dir), dir)
}

/// Create an issue and return its ID
pub fn create_issue(beads_dir: &Path, title: &str) -> String {
    let output = br_cmd(beads_dir)
        .args(["create", title, "--json"])
        .output()
        .unwrap();

    assert!(output.status.success(), "Failed to create issue: {:?}",
            String::from_utf8_lossy(&output.stderr));

    let json: serde_json::Value = serde_json::from_slice(&output.stdout).unwrap();
    json["id"].as_str().unwrap().to_string()
}
