    let id = create_issue(&beads_dir, "Last touched");

    br_cmd(&beads_dir)
        .args(["close", &id])
        .assert()
        .success();

    br_cmd(&beads_dir)
        .args(["show", &id])
        .assert()
        .success();

    // Reopen without ID uses last-touched
    br_cmd(&beads_dir)
        .arg("reopen")
        .assert()
        .success();
}
