#[test]
fn test_full_quick_example_workflow() {
    let temp = TempDir::new().unwrap();

    // Initialize
    run_in_dir(&temp, "br init").unwrap();

    // Create issue with feature type
    run_in_dir(&temp, "br create --title 'Implement user authentication' --type feature --priority 1").unwrap();

    // Create dependency
    run_in_dir(&temp, "br create --title 'Set up database schema' --type task").unwrap();

    // Get issue IDs from list
    let list_output = run_in_dir(&temp, "br list --json").unwrap();
    let issues: Value = serde_json::from_str(&list_output).unwrap();
    let ids: Vec<&str> = issues["issues"]
        .as_array()
        .unwrap()
        .iter()
        .map(|i| i["id"].as_str().unwrap())
        .collect();

    // Add dependency
    run_in_dir(&temp, &format!("br dep add {} {}", ids[0], ids[1])).unwrap();

    // Check ready (schema should be ready, auth blocked)
    let ready_output = run_in_dir(&temp, "br ready --json").unwrap();
    let ready: Value = serde_json::from_str(&ready_output).unwrap();
    assert!(ready["issues"].as_array().unwrap().len() >= 1);

    // Claim work
    run_in_dir(&temp, &format!("br update {} --status in_progress", ids[1])).unwrap();

    // Complete and sync
    run_in_dir(&temp, &format!("br close {}", ids[1])).unwrap();
    run_in_dir(&temp, "br sync --flush-only").unwrap();

    // Verify JSONL exists
    assert!(temp.path().join(".beads/issues.jsonl").exists());
}
